import React from 'react';

const ReadingGoal = (props) => {

	const setReadingGoalTry = (input) => {

		if(isNaN(input) || input <= 0){
			//Invalid reading goal page count
			window.alert('Invalid goal page count!')
			props.setReadingGoal();
			return;
		}
		props.setReadingGoal(Number(input));
	}

	return(
		<div>
			<p>Input your reading goal as a number of pages/day read to maintain your reading streak:<input type='text' placeholder='pages/day...' 
			onChange={event => setReadingGoalTry(event.target.value)} /></p>
			<button onClick={() => props.setAppState('start')}>Confirm new reading goal of {props.readingGoal} pages/day?</button>
		</div>
	)
}

export default ReadingGoal;
